local function EoCAtlasGetQuestList()
	SendAddonMessage("EoCAtlas", "QuestList", "WHISPER", UnitName("player"));
end

local function EoCAtlasUpdateRequest()
	SendAddonMessage("EoCAtlas", "Update:"..tostring(EOC_ATLAS.version), "WHISPER", UnitName("player"));
end

local addonQuest = "QuestList:";
local addonQuestList = {};
local addonUpdate = "AtlasUpdate:";
local addonUpdateList = {"assert(EOC_ATLAS);"};

function EoCAtlasFrame_OnEvent(self, event, ...)
	if (event == "VARIABLES_LOADED") then
		-- if (not EOC_ATLAS) then
			EoCAtlasLoadInitialData();
			-- EoCAtlasGetQuestList();
		-- end
		-- EoCAtlasUpdateRequest();
	elseif (event == "CHAT_MSG_ADDON") then
		local msg, unk, channel, from = ...;
		if (channel == "WHISPER" and from == UnitName("player")) then
			if (msg:sub(1, #addonQuest) == addonQuest) then
				if (msg:sub(-1) == "\1") then
					table.insert(addonQuestList, msg:sub(#addonQuest+1, -2));
					local list = {strsplit(",", table.concat(addonQuestList))};
					for k, v in next, list do
						EOC_ATLAS.completed[tonumber(v)] = true;
					end
					addonQuestList = {};
				else
					table.insert(addonQuestList, msg:sub(#addonQuest+1));
				end
			elseif (msg:sub(1, #addonUpdate) == addonUpdate) then
				if (msg:sub(-1) == "\1") then
					table.insert(addonUpdateList, msg:sub(#addonUpdate+1, -2));
					local func, err = loadstring(table.concat(addonUpdateList));
					assert(loadstring(table.concat(addonUpdateList)))();
					addonUpdateList = {"assert(EOC_ATLAS);"};
				else
					table.insert(addonUpdateList, msg:sub(#addonUpdate+1));
				end
			end
		end
	elseif (event == "QUEST_COMPLETE") then
		local text = GetTitleText();
		local i = 1;
		while GetQuestLogTitle(i) do
			local title,_,_,_,isHeader,_,_,_,questID = GetQuestLogTitle(i);
			if (not isHeader and title == text) then
				addonQuest = questID;
				break;
			end
			i = i + 1;
		end
	end
end

local function OnQuestRewarded()
	EOC_ATLAS.completed[addonQuest] = true;
end
hooksecurefunc("GetQuestReward", OnQuestRewarded);