Scripts.OnTextChanged:
if ( self:GetText() ~= "" ) then
	C_USERNAME:Hide();
else
	C_USERNAME:Show();
end
CreateAccountTextChange();

if ( self:GetText() ~= "" ) then
	C_EMAIL:Hide();
else
	C_EMAIL:Show();
end
CreateAccountTextChange();

if ( self:GetText() ~= "" ) then
	C_PASSWORD:Hide();
else
	C_PASSWORD:Show();
end
CreateAccountTextChange();

if ( self:GetText() ~= "" ) then
	CC_PASSWORD:Hide();
else
	CC_PASSWORD:Show();
end
CreateAccountTextChange();

Scripts.OnEnterPressed:
if ( TOSFrame:IsShown() ) then
	return;
end
AccountLogin_Login();

Scripts.OnTextChanged:
if ( self:GetText() ~= "" ) then
	AccountLoginAccountEditFill:Hide();
else
	AccountLoginAccountEditFill:Show();
end

Scripts.OnTabPressed:
if ( AccountLoginTokenEdit:IsShown() ) then
	AccountLoginTokenEdit:SetFocus();
else
	AccountLogin_FocusAccountName();
end

Scripts.OnEnterPressed:
if ( TOSFrame:IsShown() ) then
	return;
end
AccountLogin_Login();

if ( TOSFrame:IsShown() ) then
	return;
end
AccountLogin_Login();

Scripts.OnLoad:
GlueDropDownMenu_SetWidth(1, self);
AccountListDropDownLeft:SetAlpha(0);
AccountListDropDownMiddle:SetAlpha(0);
AccountListDropDownRight:SetAlpha(0);
AccountListDropDown:SetScale(1.2);
GlueDropDownMenu_Initialize(self, AccountListDropDown_Initialize);
AccountListDropDownText:SetJustifyH("LEFT");

Scripts.OnClick:
PlaySound("gsTitleOptions");

if LQF_shown then
	LoginscreenQualityFrame:Hide();
	LQF_shown = nil;
else
	LoginscreenQualityFrame:Show();
	LQF_shown = true;
end

Scripts.OnLoad:
local accountName = "";
local accN = {};
gsub(GetSavedAccountName(), "%w+", function(w) tinsert(accN, w); end)
local count = getn(accN);
if count>1 then
	accountName = accN[1];
end

if ( accountName ~= "" ) then
	self:SetChecked(1);
else
	self:SetChecked(0);
end

Scripts.OnClick:
if ( self:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end

Scripts.OnShow:
GlueFrameFadeIn(self, LOGIN_FADE_IN);

local accountName = "";
local accN = {};
gsub(GetSavedAccountName(), "%w+", function(w) tinsert(accN, w); end)
local count = getn(accN);
if count>1 then
	accountName = accN[1];
end

if ( accountName == "" ) then
	AccountLogin_FocusAccountName();
else
	AccountLogin_FocusPassword();
end

if ( not IsSystemSupported() ) then
	GlueDialog_Show("SYSTEM_INCOMPATIBLE_SSE");
end

Scripts.OnScrollRangeChanged:
GlueScrollFrame_OnScrollRangeChanged(self, yrange);
if ( not TOSScrollFrame.loaded ) then
	TOSAccept:Disable();
	TOSScrollFrame.loaded = true;
end
AccountLogin_UpdateAcceptButton(self, TOSAccepted, "TOS");

Scripts.OnClick:
if ( TOSFrame.noticeType == "TOS" ) then
	AcceptTOS();
elseif ( TOSFrame.noticeType == "EULA" ) then
	AcceptEULA();
elseif ( TOSFrame.noticeType == "TERMINATION" ) then
	AcceptTerminationWithoutNotice();
elseif ( TOSFrame.noticeType == "SCAN" ) then
	AcceptScanning();
elseif ( TOSFrame.noticeType == "CONTEST" ) then
	AcceptContest();
end
self:GetParent():Hide();
TOSNotice:Hide();
TOSAccept:Disable();
AccountLogin_ShowUserAgreements();

Scripts.OnVerticalScroll:
local scrollbar = _G[self:GetName().."ScrollBar"];
scrollbar:SetValue(offset);
local min;
local max;
min, max = scrollbar:GetMinMaxValues();
if ( offset == 0 ) then
	_G[scrollbar:GetName().."ScrollUpButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollUpButton"]:Enable();
end
-- HACK: scrollbars do not handle max properly
-- DO NOT CHANGE - without speaking to Mikros/Barris/Thompson
if (scrollbar:GetValue() >= max - 20) then
	_G[scrollbar:GetName().."ScrollDownButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollDownButton"]:Enable();
end
